import React, { Component } from "react";
import FormField from "./formField";

class Form extends Component {
  constructor() {
    super();
    this.state = {
      data: [
        {
          tag: "input",
          name: "first_name",
          type: "text",
          human_label: "First Name",
        },
        {
          tag: "input",
          name: "last_name",
          type: "text",
          human_label: "Last Name",
        },
        {
          tag: "input",
          name: "email",
          type: "email",
          human_label: "Email Address",
        },
        {
          tag: "input",
          name: "phone_number",
          type: "text",
          human_label: "Phone Number",
        },
        {
          tag: "input",
          name: "job_title",
          type: "text",
          human_label: "Job Title",
        },
        {
          tag: "input",
          name: "date_of_birth",
          type: "date",
          human_label: "Date of Birth",
        },
        {
          tag: "input",
          name: "parental_consent",
          type: "checkbox",
          human_label: "Parental Consent",
          conditional: {
            name: "date_of_birth",
            show_if: (value) => {
              const now = new Date();
              return (
                value >=
                new Date(now.getFullYear() - 13, now.getMonth(), now.getDate())
              );
            },
          },
        },
      ],
    };
  }

  render() {
    return (
      <form>
        {this.state.data.map((data) => (
          <div key={data.name}>
            {" "}
            {data.human_label} <input type="text" />{" "}
          </div>
        ))}
      </form>
    );
  }
}

export default Form;
